    private void jButtonSalvaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSalvaActionPerformed
            String nome = jTextFieldNome.getText();
            String nacionalidade = jComboBoxNacionalidade.getSelectedItem().toString();
            boolean sucesso = false;
            
            try
            {
                 AtorController atorController = new AtorController();
                sucesso = atorController.CadastraAtor(nome, nacionalidade);
                if(sucesso == true)
                {
                    JOptionPane.showMessageDialog(null, "O cadastro de ator foi cuncluido com sucesso!");
                    this.jButtonLimpaActionPerformed(evt);
                }
                else
                {
                    JOptionPane.showMessageDialog(null, "Ocorreu um erro durante o cadastro de ator");
                }
            }
            catch(ExceptionDAO E)
            {
                JOptionPane.showMessageDialog(null, "Ocorreu um erro ao tentar cadastrar o ator" + E);
            } catch (SQLException ex) {
            Logger.getLogger(TelaCadastroAtor.class.getName()).log(Level.SEVERE, null, ex);
        } catch (ParseException ex) {
            Logger.getLogger(TelaCadastroAtor.class.getName()).log(Level.SEVERE, null, ex);
        }
            
    }//GEN-LAST:event_jButtonSalvaActionPerformed

